import React, { useState } from 'react';
import NiveisForm from '../components/NiveisForm';
import NiveisList from '../components/NiveisForm';
import { FaLayerGroup } from 'react-icons/fa';

function CadastroNiveis() {
  const [niveis, setNiveis] = useState([]);
  const [editandoId, setEditandoId] = useState(null);
  const [refresh, setRefresh] = useState(0);

  // Função chamada pela Lista para avisar ao Pai que queremos editar
  const handleEditClick = (id) => {
    setEditandoId(id);
  };

  const handleCancelEdit = () => {
    setEditandoId(null);
  };

  const handleSuccess = () => {
    setRefresh(refresh + 1); // Dispara a atualização da lista
    setEditandoId(null);     // Sai do modo edição
  };

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
        <FaLayerGroup className="text-blue-600" />
        Gestão de Níveis de Acesso
      </h1>

      <NiveisForm
        onSuccess={handleSuccess}
        idParaEditar={editandoId}
        onCancel={handleCancelEdit}
      />

      <NiveisList
        refresh={refresh}
        onEditClick={handleEditClick}
      />
    </div>
  );
}

export default CadastroNiveis;
