import React, { useState, useEffect } from 'react';
import { FaTimes, FaSave, FaPlus } from 'react-icons/fa';

function NiveisForm({ onSuccess, idParaEditar, onCancel }) {
  const [formData, setFormData] = useState({ id_nivel: '', nivel: '', descricao: '' });

  useEffect(() => {
    if (idParaEditar) {
      const token = localStorage.getItem('token');
      fetch(`/api/niveis/${idParaEditar}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      .then(res => res.json())
      .then(data => {
        // O backend costuma retornar um objeto direto ou o primeiro item de um array
        const registro = Array.isArray(data) ? data[0] : data; 
        
        if (registro) {
          setFormData({ 
            nivel: registro.nivel || '', 
            descricao: registro.descricao || '' 
          });
        }
      })
      .catch(err => console.error("Erro ao carregar nível", err));
    } else {
      setFormData({ nivel: '', descricao: '' });
    }
  }, [idParaEditar]);
  



  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = idParaEditar ? 'PUT' : 'POST';
    const url = idParaEditar ? `/api/niveis/${idParaEditar}` : '/api/niveis';

    try {
      const response = await fetch(url, {
        method,
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}` 
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        onSuccess();
      }
    } catch (error) {
      console.error("Erro na operação", error);
    }
  };

  return (
    <div className={`p-6 rounded-lg shadow-md mb-8 transition-all border-l-8 
      ${idParaEditar ? 'bg-orange-50 border-orange-500' : 'bg-white border-blue-500'}`}>
      
      <h3 className={`font-bold text-lg mb-4 ${idParaEditar ? 'text-orange-700' : 'text-blue-700'}`}>
        {idParaEditar ? '✏️ Modo de Edição' : '➕ Cadastrar Novo Nível'}
      </h3>

      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        
{/* Campo Nome */}
<input 
  type="text" 
  value={formData.nivel}
  onChange={(e) => setFormData({...formData, nivel: e.target.value})} // Simplificado
  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none"
  required
/>

{/* Campo Descrição */}
<input 
  type="text" 
  value={formData.descricao}
  onChange={(e) => setFormData({...formData, descricao: e.target.value})} // Simplificado
  className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none"
/>

        {/* Grupo de Botões */}
        <div className="md:col-span-1 flex gap-2">
          {idParaEditar ? (
            <>
              <button 
                type="submit"
                className="flex-1 bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaSave /> Salvar
              </button>
              <button 
                type="button"
                onClick={onCancel}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaTimes /> Sair
              </button>
            </>
          ) : (
            <button 
              type="submit"
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
            >
              <FaPlus /> Adicionar
            </button>
          )}
        </div>

      </form>
    </div>
  );
}

export default NiveisForm;
