import React, { useState, useEffect } from 'react';
import { FaTimes, FaSave, FaPlus } from 'react-icons/fa';

function NiveisForm({ onSuccess, idParaEditar, onCancel }) {
  const [formData, setFormData] = useState({ id_nivel: '', nivel: '', descricao: '' });

  useEffect(() => {
    if (!idParaEditar) {
      setFormData({ id_nivel: '', nivel: '', descricao: '' });
      return;
    }
  
    const token = localStorage.getItem('token');
  
    fetch(`/api/niveis/${idParaEditar}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        if (!res.ok) throw new Error('Erro ao buscar nível');
        return res.json();
      })
      .then(data => {
        setFormData({
          id_nivel: data.id_nivel,
          nivel: data.nivel,
          descricao: data.descricao || ''
        });
      })
      .catch(err => console.error('Erro ao carregar nível:', err));
  }, [idParaEditar]);
  



  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = idParaEditar ? 'PUT' : 'POST';
    const url = idParaEditar ? `/api/niveis/${idParaEditar}` : '/api/niveis';

    try {
      const response = await fetch(url, {
        method,
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}` 
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        onSuccess();
      }
    } catch (error) {
      console.error("Erro na operação", error);
    }
  };

  return (
    <div className={`p-6 rounded-lg shadow-md mb-8 transition-all border-l-8 
      ${idParaEditar ? 'bg-orange-50 border-orange-500' : 'bg-white border-blue-500'}`}>
      
      <h3 className={`font-bold text-lg mb-4 ${idParaEditar ? 'text-orange-700' : 'text-blue-700'}`}>
        {idParaEditar ? '✏️ Modo de Edição' : '➕ Cadastrar Novo Nível'}
      </h3>

      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        
        {/* Campo Nome */}
        <div className="md:col-span-1">
          <label className="block text-sm font-semibold text-gray-600 mb-1">Nome do Nível</label>
          <input 
            type="text" 
            value={formData.nivel}
            onChange={(e) => setFormData({...formData, id_nivel: formData.id_nivel,nivel: e.target.value, descricao: formData.descricao})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none transition-all"
            placeholder="Ex: Gerente"
            required
          />
        </div>

        {/* Campo Descrição */}
        <div className="md:col-span-2">
          <label className="block text-sm font-semibold text-gray-600 mb-1">Descrição Curta</label>
          <input 
            type="text" 
            value={formData.descricao}
            onChange={(e) => setFormData({...formData, id_nivel: formData.id_nivel,nivel: formData.nivel, descricao: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none transition-all"
            placeholder="O que este nível pode fazer?"
          />
        </div>

        {/* Grupo de Botões */}
        <div className="md:col-span-1 flex gap-2">
          {idParaEditar ? (
            <>
              <button 
                type="submit"
                className="flex-1 bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaSave /> Salvar
              </button>
              <button 
                type="button"
                onClick={onCancel}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaTimes /> Sair
              </button>
            </>
          ) : (
            <button 
              type="submit"
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
            >
              <FaPlus /> Adicionar
            </button>
          )}
        </div>

      </form>
    </div>
  );
}

export default NiveisForm;
