import React, { useState, useEffect } from 'react';
import { FaTimes, FaSave, FaPlus } from 'react-icons/fa';

function NiveisForm({ onSuccess, idParaEditar, onCancel }) {
  const [formData, setFormData] = useState({ nivel: '', descricao: '' });

  // Busca dados do nível caso esteja em modo edição
  useEffect(() => {
    if (idParaEditar) {
      fetch(`/api/niveis/${idParaEditar}`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      })
      .then(res => res.json())
      .then(data => setFormData({ nivel: data.nivel, descricao: data.descricao || '' }))
      .catch(err => console.error("Erro ao carregar nível", err));
    } else {
      setFormData({ nivel: '', descricao: '' });
    }
  }, [idParaEditar]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = idParaEditar ? 'PUT' : 'POST';
    const url = idParaEditar ? `/api/niveis/${idParaEditar}` : '/api/niveis';

    try {
      const response = await fetch(url, {
        method,
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}` 
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        onSuccess(); // Chama a função do pai para atualizar a lista
      }
    } catch (error) {
      console.error("Erro na operação", error);
    }
  };

  return (
    <div className={`p-6 rounded-lg shadow-md mb-8 transition-all border-2 
      ${idParaEditar ? 'bg-orange-50 border-orange-200' : 'bg-white border-transparent'}`}>
      
      <div className="flex justify-between items-center mb-4">
        <h3 className={`font-bold text-lg ${idParaEditar ? 'text-orange-600' : 'text-gray-700'}`}>
          {idParaEditar ? '✏️ Editando Nível' : '➕ Novo Nível'}
        </h3>
        {idParaEditar && (
          <button onClick={onCancel} className="text-sm text-gray-500 hover:text-red-500 flex items-center gap-1">
            <FaTimes /> Cancelar
          </button>
        )}
      </div>

      <form onSubmit={handleSubmit} className="flex gap-4 items-end flex-wrap">
        <div className="flex-1 min-w-[200px]">
          <label className="block text-sm font-medium text-gray-700 mb-1">Nome do Nível *</label>
          <input 
            type="text" 
            value={formData.nivel}
            onChange={(e) => setFormData({...formData, nivel: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 outline-none"
            required
          />
        </div>

        <div className="flex-[2] min-w-[300px]">
          <label className="block text-sm font-medium text-gray-700 mb-1">Descrição</label>
          <input 
            type="text" 
            value={formData.descricao}
            onChange={(e) => setFormData({...formData, descricao: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 outline-none"
          />
        </div>

        <button 
          type="submit"
          className={`px-6 py-2 rounded text-white font-bold flex items-center gap-2 transition
            ${idParaEditar ? 'bg-orange-500 hover:bg-orange-600' : 'bg-green-600 hover:bg-green-700'}`}
        >
          {idParaEditar ? <><FaSave /> Salvar</> : <><FaPlus /> Adicionar</>}
        </button>
      </form>
    </div>
  );
}

export default NiveisForm;
