import React, { useState, useEffect, useCallback } from 'react';
import { FaTrash, FaEdit, FaTimes, FaLayerGroup } from 'react-icons/fa';

function CadastroNiveis() {
  const [niveis, setNiveis] = useState([]);
  const [editandoId, setEditandoId] = useState(null);
  const [formData, setFormData] = useState({ nivel: '', descricao: '' });

  const getToken = () => localStorage.getItem('token');

  // Função de busca memorizada para evitar re-renderizações infinitas
  const fetchNiveis = useCallback(async () => {
    try {
      const response = await fetch('/api/niveis', {
        headers: { 'Authorization': `Bearer ${getToken()}` }
      });
      const data = await response.json();
      setNiveis(data);
    } catch (error) {
      console.error("Erro ao buscar níveis", error);
    }
  }, []);

  useEffect(() => {
    fetchNiveis();
  }, [fetchNiveis]);

  const handleEditClick = (item) => {
    setFormData({ nivel: item.nivel, descricao: item.descricao || '' });
    setEditandoId(item.id_nivel);
    window.scrollTo({ top: 0, behavior: 'smooth' }); // Melhora UX ao editar
  };

  const handleCancelEdit = () => {
    setFormData({ nivel: '', descricao: '' });
    setEditandoId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editandoId ? 'PUT' : 'POST';
    const url = editandoId ? `/api/niveis/${editandoId}` : '/api/niveis';

    try {
      const response = await fetch(url, {
        method,
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${getToken()}` 
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        handleCancelEdit();
        fetchNiveis();
      }
    } catch (error) {
      alert("Erro de conexão");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Deseja realmente excluir este nível?")) return;
    try {
      const response = await fetch(`/api/niveis/${id}`, { 
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${getToken()}` }
      });
      if (response.ok) fetchNiveis();
    } catch (error) {
      alert("Erro ao excluir");
    }
  };

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
        <FaLayerGroup className="text-blue-600" />
        Gestão de Níveis de Acesso
      </h1>

      {/* FORMULÁRIO PADRONIZADO */}
      <div className={`p-6 rounded-lg shadow-md mb-8 transition-all duration-300 border-2 
        ${editandoId ? 'bg-orange-50 border-orange-200' : 'bg-white border-transparent'}`}>
        
        <div className="flex justify-between items-center mb-4">
          <h3 className={`font-bold text-lg ${editandoId ? 'text-orange-600' : 'text-gray-700'}`}>
            {editandoId ? '✏️ Editando Nível' : '➕ Novo Nível'}
          </h3>
          {editandoId && (
            <button onClick={handleCancelEdit} className="text-sm text-gray-500 hover:text-red-500 flex items-center gap-1">
              <FaTimes /> Cancelar Edição
            </button>
          )}
        </div>

        <form onSubmit={handleSubmit} className="flex gap-4 items-end flex-wrap">
          <div className="flex-1 min-w-[200px]">
            <label className="block text-sm font-medium text-gray-700 mb-1">Nome do Nível *</label>
            <input 
              type="text" 
              value={formData.nivel}
              onChange={(e) => setFormData({...formData, nivel: e.target.value})}
              className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 outline-none"
              required
            />
          </div>

          <div className="flex-[2] min-w-[300px]">
            <label className="block text-sm font-medium text-gray-700 mb-1">Descrição</label>
            <input 
              type="text" 
              value={formData.descricao}
              onChange={(e) => setFormData({...formData, descricao: e.target.value})}
              className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 outline-none"
            />
          </div>

          <button 
            type="submit"
            className={`px-6 py-2 rounded text-white font-bold h-[42px] transition transform active:scale-95
              ${editandoId ? 'bg-orange-500 hover:bg-orange-600' : 'bg-green-600 hover:bg-green-700'}`}
          >
            {editandoId ? 'Salvar Alterações' : 'Adicionar'}
          </button>
        </form>
      </div>

      {/* TABELA PADRONIZADA */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-200">
            <tr>
              <th className="p-4 text-gray-600 font-semibold w-20">ID</th>
              <th className="p-4 text-gray-600 font-semibold">Nível</th>
              <th className="p-4 text-gray-600 font-semibold">Descrição</th>
              <th className="p-4 text-gray-600 font-semibold text-center w-32">Ações</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {niveis.map((item) => (
              <tr key={item.id_nivel} className="hover:bg-blue-50/50 transition-colors group">
                <td className="p-4 text-gray-400 text-sm">#{item.id_nivel}</td>
                <td className="p-4 font-semibold text-gray-700">{item.nivel}</td>
                <td className="p-4 text-gray-500 text-sm italic">{item.descricao || '---'}</td>
                <td className="p-4">
                  <div className="flex justify-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                    <button onClick={() => handleEditClick(item)} className="p-2 text-yellow-600 hover:bg-yellow-100 rounded">
                      <FaEdit />
                    </button>
                    <button onClick={() => handleDelete(item.id_nivel)} className="p-2 text-red-600 hover:bg-red-100 rounded">
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroNiveis;
