import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash } from 'react-icons/fa';

function NiveisList({ refresh, onEditClick }) {
  const [niveis, setNiveis] = useState([]);

  useEffect(() => {
    fetch('/api/niveis', {
      headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
    })
      .then(res => res.json())
      .then(data => setNiveis(data))
      .catch(err => console.error("Erro ao buscar níveis", err));
  }, [refresh]); // Recarrega sempre que 'refresh' muda no pai

  const handleDelete = async (id) => {
    if (!window.confirm("Excluir este nível?")) return;
    // Lógica de delete aqui...
    const res = await fetch(`/api/niveis/${id}`, { 
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
    });
    if(res.ok) {
        // Você pode disparar um refresh manual ou filtrar o estado local
        setNiveis(niveis.filter(n => n.id_nivel !== id));
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
      <table className="w-full text-left">
        <thead className="bg-gray-50 border-b">
          <tr>
            <th className="p-4 text-gray-600 font-semibold w-20">ID</th>
            <th className="p-4 text-gray-600 font-semibold">Nível</th>
            <th className="p-4 text-gray-600 font-semibold text-center w-32">Ações</th>
          </tr>
        </thead>
        <tbody className="divide-y divide-gray-100">
          {niveis.map((item) => (
            <tr key={item.id_nivel} className="hover:bg-gray-50 transition-colors group">
              <td className="p-4 text-gray-400">#{item.id_nivel}</td>
              <td className="p-4">
                <div className="font-bold text-gray-700">{item.nivel}</div>
                <div className="text-xs text-gray-400 italic">{item.descricao}</div>
              </td>
              <td className="p-4">
                <div className="flex justify-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button onClick={() => onEditClick(item.id_nivel)} className="p-2 text-yellow-600 hover:bg-yellow-100 rounded">
                    <FaEdit />
                  </button>
                  <button onClick={() => handleDelete(item.id_nivel)} className="p-2 text-red-600 hover:bg-red-100 rounded">
                    <FaTrash />
                  </button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
