import React, { useState, useEffect } from 'react';
import { FaSave, FaTimes, FaPlus } from 'react-icons/fa';
import { Toast } from '../utils/swalHelpers';

function CursosForm({ onSuccess, cursoParaEditar, onCancel }) {
  const [formData, setFormData] = useState({
    nome_curso: '',
    descricao: '',
    carga_horaria: ''
  });

  // Monitora se há um curso para editar e preenche o formulário
  useEffect(() => {
    if (cursoParaEditar) {
      setFormData({
        nome_curso: cursoParaEditar.nome_curso || '',
        descricao: cursoParaEditar.descricao || '',
        carga_horaria: cursoParaEditar.carga_horaria || ''
      });
    } else {
      setFormData({ nome_curso: '', descricao: '', carga_horaria: '' });
    }
  }, [cursoParaEditar]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    
    // Define se é atualização (PUT) ou criação (POST)
    const method = cursoParaEditar ? 'PUT' : 'POST';
    const url = cursoParaEditar 
      ? `/api/cursos/${cursoParaEditar.id_curso}` 
      : '/api/cursos';

    try {
      const response = await fetch(url, {
        method,
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` 
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        Toast.fire({
          icon: 'success',
          title: cursoParaEditar ? 'Curso atualizado!' : 'Curso cadastrado!'
        });
        onSuccess();
      } else {
        const errorData = await response.json();
        alert(errorData.error || "Erro ao processar requisição");
      }
    } catch (error) {
      console.error("Erro de conexão:", error);
    }
  };

  return (
    <div className={`p-6 rounded-lg shadow-md mb-8 transition-all border-l-8 
      ${cursoParaEditar ? 'bg-orange-50 border-orange-500' : 'bg-white border-blue-500'}`}>
      
      <h3 className={`font-bold text-lg mb-4 ${cursoParaEditar ? 'text-orange-700' : 'text-blue-700'}`}>
        {cursoParaEditar ? '✏️ Editando Curso' : '➕ Cadastrar Novo Curso'}
      </h3>

      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        
        {/* Nome do Curso */}
        <div className="md:col-span-1">
          <label className="block text-sm font-semibold text-gray-600 mb-1">Nome do Curso</label>
          <input 
            type="text" 
            value={formData.nome_curso}
            onChange={(e) => setFormData({...formData, nome_curso: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none transition-all"
            placeholder="Ex: Engenharia de Software"
            required
          />
        </div>

        {/* Descrição */}
        <div className="md:col-span-1">
          <label className="block text-sm font-semibold text-gray-600 mb-1">Descrição</label>
          <input 
            type="text" 
            value={formData.descricao}
            onChange={(e) => setFormData({...formData, descricao: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none transition-all"
            placeholder="Breve resumo..."
          />
        </div>

        {/* Carga Horária */}
        <div className="md:col-span-1">
          <label className="block text-sm font-semibold text-gray-600 mb-1">Carga Horária (h)</label>
          <input 
            type="number" 
            value={formData.carga_horaria}
            onChange={(e) => setFormData({...formData, carga_horaria: e.target.value})}
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400 outline-none transition-all"
            placeholder="Ex: 40"
          />
        </div>

{/* Ações */}
<div className="md:col-span-1 flex gap-2">
          {cursoParaEditar ? (
            <>
              <button 
                type="submit"
                className="flex-1 bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaSave /> Salvar
              </button>
              <button 
                type="button"
                onClick={onCancel}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
              >
                <FaTimes /> Sair
              </button>
            </>
          ) : (
            <button 
              type="submit"
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded shadow-md transition-colors flex items-center justify-center gap-2"
            >
              <FaPlus /> Adicionar
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default CursosForm;
